import React, { Fragment } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";

const Alert = ({ alerts }) =>
  alerts.map((alert) => {
    <div key={alert.id} className={`alert alert-${alert.alertType}`}>
      {alert.msg}
    </div>;
  });
