import React, { Fragment } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";

const Alert = ({ alerts }) => {
  return;
  <div key={alerts.id} className={`alert alert-${alerts.alertType}`}>
    {alerts.msg}
  </div>;
};
